mapper.insert:
insert
  into notice
  (
  	notice_no
  , notice_title
  , notice_writer
  , notice_content
  )
  values
  (
  	seq_nno.nextval
  , #{noticeTitle}
  , #{noticeWriter}
  , #{noticeContent}
  )

insert
  into attachment
  (
  	file_no
  , file_path
  , filesystem_name
  , original_name
  , ref_type
  , ref_no
  )
  values
  (
  	seq_ano.nextval
  , #{filePath}
  , #{filesystemName}
  , #{originalName}
  , #{refType}


  		, seq_nno.currval


  		, #{refNo}


  )
